mapper.delete:
DELETE md FROM mileage_detail md
JOIN mile_introduce mi ON md.mile_score_name = mi.mile_title
WHERE mi.mile_introduce_no = #{mile_introduce_no};

mapper.update:
UPDATE mileage_detail md
    JOIN mile_introduce mi ON md.mile_score_name = mi.mile_title
    SET md.job_name = #{job_name}
where mi.mile_introduce_no = #{mile_introduce_no};
