mapper.delete:
DELETE md FROM mileage_detail md
JOIN mile_introduce mi ON md.mile_score_name = mi.mile_title
WHERE mi.mile_introduce_no = #{mile_introduce_no};

mapper.update:
UPDATE mileage_detail md
    JOIN mile_introduce mi ON md.mile_score_name = mi.mile_title
    SET md.job_name = #{job_name}
where mi.mile_introduce_no = #{mile_introduce_no};

mapper.select:
SELECT DATE_FORMAT(date, '%Y-%m') AS year_and_month, COALESCE(SUM(mile_score_point), 0) AS total_score_points, COALESCE(SUM(mile_history_point), 0) AS total_history_points, COALESCE(SUM(mile_score_point), 0) + COALESCE(SUM(mile_history_point), 0) AS total_points FROM ( SELECT mile_score_date AS date, mile_score_point, NULL AS mile_history_point FROM mile_score WHERE mile_no = #{mile_no} AND YEAR(mile_score_date) IN (YEAR(CURDATE()), YEAR(CURDATE()) - 1)
UNION ALL
SELECT mile_history_date AS date, NULL AS mile_score_point, mile_history_point FROM mile_history WHERE mile_no = #{mile_no} AND YEAR(mile_history_date) IN (YEAR(CURDATE()), YEAR(CURDATE()) - 1) ) AS combined GROUP BY DATE_FORMAT(date, '%Y-%m') ORDER BY DATE_FORMAT(date, '%Y-%m');
