mapper.select:
SELECT COUNT(*)
FROM hit_mile
WHERE mile_no = #{mile_no}
  AND hit_date = CURRENT_DATE;

mapper.update:
UPDATE hit_mile
SET hit_count = hit_count + 1
WHERE mile_no = #{mile_no} and hit_date = CURRENT_DATE;

mapper.select:
SELECT m.mile_no, m.mile_name, COALESCE(h.hit_count, 0) AS hit_count
FROM mileage m
         LEFT JOIN (
    SELECT mile_no, hit_count
    FROM hit_mile
    WHERE hit_date = #{date}
) h ON m.mile_no = h.mile_no
where m.mile_is_delete = 0
ORDER BY m.mile_no;

SELECT *
FROM mile_recommand
WHERE mile_no = (
    SELECT mile_no
    FROM mile_score
    WHERE user_no = #{userNo}
    GROUP BY mile_no
    ORDER BY SUM(mile_score_point) ASC
    LIMIT 1
    );

WITH recent_month_scores AS ( SELECT ms.user_no, SUM(ms.mile_score_point) AS recent_month_total_point, MAX(ms.mile_score_date) AS base_date FROM mile_score ms WHERE YEAR(ms.mile_score_date) = YEAR(CURDATE() - INTERVAL 1 DAY) AND MONTH(ms.mile_score_date) = MONTH(CURDATE() - INTERVAL 1 DAY) GROUP BY ms.user_no ), recent_previous_month_scores AS ( SELECT mh.user_no, SUM(mh.mile_history_point) AS recent_previous_month_total_point, MAX(mh.mile_history_date) AS base_date FROM mile_history mh WHERE YEAR(mh.mile_history_date) = YEAR(CURDATE() - INTERVAL 1 DAY) AND MONTH(mh.mile_history_date) = MONTH(CURDATE() - INTERVAL 1 DAY) - 1 GROUP BY mh.user_no ), user_scores AS ( SELECT COALESCE(rms.user_no, rps.user_no) AS user_no, COALESCE(rms.recent_month_total_point, 0) AS recent_month_total_point, COALESCE(rps.recent_previous_month_total_point, 0) AS recent_previous_month_total_point, COALESCE(rms.base_date, rps.base_date) AS base_date FROM recent_month_scores rms LEFT JOIN recent_previous_month_scores rps ON rms.user_no = rps.user_no
UNION ALL
SELECT COALESCE(rms.user_no, rps.user_no) AS user_no, COALESCE(rms.recent_month_total_point, 0) AS recent_month_total_point, COALESCE(rps.recent_previous_month_total_point, 0) AS recent_previous_month_total_point, COALESCE(rms.base_date, rps.base_date) AS base_date FROM recent_month_scores rms RIGHT JOIN recent_previous_month_scores rps ON rms.user_no = rps.user_no WHERE rms.user_no IS NULL ) SELECT RANK() OVER (ORDER BY (us.recent_month_total_point - us.recent_previous_month_total_point) DESC) AS ranking, u.user_name, us.recent_month_total_point - us.recent_previous_month_total_point AS score_increase, us.base_date FROM user_scores us JOIN user u ON us.user_no = u.user_no ORDER BY ranking LIMIT 5;

WITH recent_previous_month_scores AS ( SELECT mh.user_no, SUM(mh.mile_history_point) AS recent_previous_month_total_point, MAX(mh.mile_history_date) AS base_date FROM mile_history mh WHERE YEAR(mh.mile_history_date) = YEAR(CURDATE()) AND MONTH(mh.mile_history_date) = MONTH(CURDATE()) - 1 GROUP BY mh.user_no ), two_months_ago_scores AS ( SELECT mh.user_no, SUM(mh.mile_history_point) AS two_months_ago_total_point, MAX(mh.mile_history_date) AS base_date FROM mile_history mh WHERE YEAR(mh.mile_history_date) = YEAR(CURDATE()) AND MONTH(mh.mile_history_date) = MONTH(CURDATE()) - 2 GROUP BY mh.user_no ), user_scores AS ( SELECT COALESCE(rpm.user_no, tma.user_no) AS user_no, COALESCE(rpm.recent_previous_month_total_point, 0) AS recent_previous_month_total_point, COALESCE(tma.two_months_ago_total_point, 0) AS two_months_ago_total_point, COALESCE(rpm.base_date, tma.base_date) AS base_date FROM recent_previous_month_scores rpm LEFT JOIN two_months_ago_scores tma ON rpm.user_no = tma.user_no
UNION
SELECT COALESCE(rpm.user_no, tma.user_no) AS user_no, COALESCE(rpm.recent_previous_month_total_point, 0) AS recent_previous_month_total_point, COALESCE(tma.two_months_ago_total_point, 0) AS two_months_ago_total_point, COALESCE(rpm.base_date, tma.base_date) AS base_date FROM recent_previous_month_scores rpm RIGHT JOIN two_months_ago_scores tma ON rpm.user_no = tma.user_no WHERE rpm.user_no IS NULL ) SELECT RANK() OVER (ORDER BY (us.recent_previous_month_total_point - us.two_months_ago_total_point) DESC) AS ranking, u.user_name, us.recent_previous_month_total_point - us.two_months_ago_total_point AS score_increase, us.base_date, us.user_no FROM user_scores us JOIN user u ON us.user_no = u.user_no ORDER BY ranking LIMIT 5;

UPDATE mileage
SET mile_is_delete = 1
WHERE mile_no =  #{mileNo};

mapper.update:
UPDATE mileage
SET mile_name = #{mileNameInput}
WHERE mile_no = #{mileNo};

UPDATE mileage
SET mile_max = #{mileMax}
WHERE mile_no = #{mileNo};
